mapper.select:
SELECT com_no, member_no, com_title, com_content, com_views, com_created_at
FROM COMMUNITY
where com_no=#{comNo}

SELECT com_no, member_no, com_title, com_content, com_views, com_created_at
FROM COMMUNITY
where member_no=#{memberNo}

mapper.update:
UPDATE COMMUNITY
SET
	com_title = #{title},
	com_content = #{content}
WHERE com_no=#{comNo}
